pkg load databaseconn = pq_connect (setdbopts("dbname","tabla_precios"));precio=input("ingrese el precio de su producto en quetzales");IVA = precio *0.12;sinIVA= precio - IVA;fprintf("el precio del producto sin iva es de Q%0.0f , por lo que el IVA es de Q%0.0f\n" , sinIVA , IVA);try   Ins1 = 'insert into tabla_precios (precios) values( ';  Ins2 = ");";  Instruccion = strcat(Ins1, num2str(precio), Ins2)  Registro = pq_exec_params(conn, Instruccion);catch e  disp(['Error durante la conexion a la DB, consulte sobre el error: ' e.message]);  end